import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

const API_BASE = process.env.REACT_APP_API_URL || 'http://localhost:8000';

function App() {
  const [activeTab, setActiveTab] = useState('finder');
  const [loading, setLoading] = useState(false);
  const [results, setResults] = useState([]);
  const [error, setError] = useState(null);

  // Email Finder state
  const [finderForm, setFinderForm] = useState({
    first_name: '',
    last_name: '',
    domain: ''
  });

  // Email Verifier state
  const [verifierForm, setVerifierForm] = useState({
    email: ''
  });

  // CSV upload state
  const [csvFile, setCsvFile] = useState(null);
  const [csvType, setCsvType] = useState('find'); // 'find' or 'verify'

  const handleFindEmail = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    setResults([]);

    try {
      const response = await axios.post(`${API_BASE}/api/find`, finderForm, {
        timeout: 30000 // 30 second timeout
      });
      setResults(response.data);
    } catch (err) {
      if (err.code === 'ECONNABORTED') {
        setError('Request timed out. Please check if the backend server is running.');
      } else if (err.code === 'ERR_NETWORK' || err.message.includes('Network Error')) {
        setError('Cannot connect to backend server. Make sure it\'s running on http://localhost:8000');
      } else {
        setError(err.response?.data?.detail || err.message || 'An error occurred');
      }
    } finally {
      setLoading(false);
    }
  };

  const handleVerifyEmail = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    setResults([]);

    try {
      const response = await axios.post(`${API_BASE}/api/verify`, verifierForm);
      setResults([response.data]);
    } catch (err) {
      setError(err.response?.data?.detail || err.message || 'An error occurred');
    } finally {
      setLoading(false);
    }
  };

  const handleCsvUpload = async (e) => {
    e.preventDefault();
    if (!csvFile) {
      setError('Please select a CSV file');
      return;
    }

    setLoading(true);
    setError(null);
    setResults([]);

    try {
      const formData = new FormData();
      formData.append('file', csvFile);

      const endpoint = csvType === 'find' 
        ? `${API_BASE}/api/bulk-find`
        : `${API_BASE}/api/bulk-verify`;

      const response = await axios.post(endpoint, formData, {
        responseType: 'blob',
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      // Create download link
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', response.headers['content-disposition']?.split('filename=')[1] || 'results.csv');
      document.body.appendChild(link);
      link.click();
      link.remove();

      setError(null);
      alert('CSV file downloaded successfully!');
    } catch (err) {
      setError(err.response?.data?.detail || err.message || 'An error occurred');
    } finally {
      setLoading(false);
    }
  };

  const getStatusBadge = (status) => {
    const badges = {
      'valid': 'success',
      'invalid': 'danger',
      'catch-all': 'warning',
      'unknown': 'secondary',
      'not_found': 'info',
      'error': 'danger'
    };
    return badges[status] || 'secondary';
  };

  return (
    <div className="App">
      <div className="container mt-4">
        <div className="text-center mb-4">
          <h1 className="display-4">ðŸ“§ Email Finder & Verifier</h1>
          <p className="lead">Find and verify email addresses with confidence</p>
        </div>

        {/* Tabs */}
        <ul className="nav nav-tabs mb-4" role="tablist">
          <li className="nav-item">
            <button
              className={`nav-link ${activeTab === 'finder' ? 'active' : ''}`}
              onClick={() => setActiveTab('finder')}
            >
              Email Finder
            </button>
          </li>
          <li className="nav-item">
            <button
              className={`nav-link ${activeTab === 'verifier' ? 'active' : ''}`}
              onClick={() => setActiveTab('verifier')}
            >
              Email Verifier
            </button>
          </li>
          <li className="nav-item">
            <button
              className={`nav-link ${activeTab === 'bulk' ? 'active' : ''}`}
              onClick={() => setActiveTab('bulk')}
            >
              Bulk Processing
            </button>
          </li>
        </ul>

        {/* Email Finder Tab */}
        {activeTab === 'finder' && (
          <div className="card">
            <div className="card-body">
              <h3 className="card-title mb-4">Find Email Address</h3>
              <form onSubmit={handleFindEmail}>
                <div className="row mb-3">
                  <div className="col-md-4">
                    <label htmlFor="first_name" className="form-label">First Name</label>
                    <input
                      type="text"
                      className="form-control"
                      id="first_name"
                      value={finderForm.first_name}
                      onChange={(e) => setFinderForm({...finderForm, first_name: e.target.value})}
                      required
                    />
                  </div>
                  <div className="col-md-4">
                    <label htmlFor="last_name" className="form-label">Last Name</label>
                    <input
                      type="text"
                      className="form-control"
                      id="last_name"
                      value={finderForm.last_name}
                      onChange={(e) => setFinderForm({...finderForm, last_name: e.target.value})}
                      required
                    />
                  </div>
                  <div className="col-md-4">
                    <label htmlFor="domain" className="form-label">Company Domain</label>
                    <input
                      type="text"
                      className="form-control"
                      id="domain"
                      value={finderForm.domain}
                      onChange={(e) => setFinderForm({...finderForm, domain: e.target.value})}
                      placeholder="example.com"
                      required
                    />
                  </div>
                </div>
                <button type="submit" className="btn btn-primary" disabled={loading}>
                  {loading ? (
                    <>
                      <span className="spinner-border spinner-border-sm me-2" role="status"></span>
                      Finding...
                    </>
                  ) : (
                    'Find Email'
                  )}
                </button>
              </form>
            </div>
          </div>
        )}

        {/* Email Verifier Tab */}
        {activeTab === 'verifier' && (
          <div className="card">
            <div className="card-body">
              <h3 className="card-title mb-4">Verify Email Address</h3>
              <form onSubmit={handleVerifyEmail}>
                <div className="row mb-3">
                  <div className="col-md-8">
                    <label htmlFor="email" className="form-label">Email Address</label>
                    <input
                      type="email"
                      className="form-control"
                      id="email"
                      value={verifierForm.email}
                      onChange={(e) => setVerifierForm({...verifierForm, email: e.target.value})}
                      placeholder="john.doe@example.com"
                      required
                    />
                  </div>
                </div>
                <button type="submit" className="btn btn-primary" disabled={loading}>
                  {loading ? (
                    <>
                      <span className="spinner-border spinner-border-sm me-2" role="status"></span>
                      Verifying...
                    </>
                  ) : (
                    'Verify Email'
                  )}
                </button>
              </form>
            </div>
          </div>
        )}

        {/* Bulk Processing Tab */}
        {activeTab === 'bulk' && (
          <div className="card">
            <div className="card-body">
              <h3 className="card-title mb-4">Bulk Processing</h3>
              <form onSubmit={handleCsvUpload}>
                <div className="mb-3">
                  <label htmlFor="csv_type" className="form-label">Processing Type</label>
                  <select
                    className="form-select"
                    id="csv_type"
                    value={csvType}
                    onChange={(e) => setCsvType(e.target.value)}
                  >
                    <option value="find">Find Emails (requires: first_name, last_name, domain)</option>
                    <option value="verify">Verify Emails (requires: email)</option>
                  </select>
                </div>
                <div className="mb-3">
                  <label htmlFor="csv_file" className="form-label">CSV File</label>
                  <input
                    type="file"
                    className="form-control"
                    id="csv_file"
                    accept=".csv"
                    onChange={(e) => setCsvFile(e.target.files[0])}
                    required
                  />
                  <div className="form-text">
                    {csvType === 'find' 
                      ? 'CSV must have columns: first_name, last_name, domain'
                      : 'CSV must have column: email'}
                  </div>
                </div>
                <button type="submit" className="btn btn-primary" disabled={loading}>
                  {loading ? (
                    <>
                      <span className="spinner-border spinner-border-sm me-2" role="status"></span>
                      Processing...
                    </>
                  ) : (
                    'Upload & Process'
                  )}
                </button>
              </form>
            </div>
          </div>
        )}

        {/* Error Display */}
        {error && (
          <div className="alert alert-danger mt-3" role="alert">
            <strong>Error:</strong> {error}
          </div>
        )}

        {/* Results Display */}
        {results.length > 0 && (
          <div className="card mt-4">
            <div className="card-body">
              <h4 className="card-title mb-3">Results</h4>
              <div className="table-responsive">
                <table className="table table-striped">
                  <thead>
                    <tr>
                      <th>Email</th>
                      <th>Status</th>
                      <th>Confidence</th>
                      <th>Reason</th>
                      {activeTab === 'verifier' && <th>Details</th>}
                    </tr>
                  </thead>
                  <tbody>
                    {results.map((result, index) => (
                      <tr key={index}>
                        <td>{result.email || 'N/A'}</td>
                        <td>
                          <span className={`badge bg-${getStatusBadge(result.status)}`}>
                            {result.status}
                          </span>
                        </td>
                        <td>
                          <strong>{(result.confidence * 100).toFixed(1)}%</strong>
                        </td>
                        <td>{result.reason || 'N/A'}</td>
                        {activeTab === 'verifier' && result.details && (
                          <td>
                            <details>
                              <summary>View Details</summary>
                              <pre className="mt-2 p-2 bg-light" style={{fontSize: '0.85rem'}}>
                                {JSON.stringify(result.details, null, 2)}
                              </pre>
                            </details>
                          </td>
                        )}
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
